import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { nanoid } from "nanoid"
import {
    addPerson
} from "../../redux/actions/person"



const Person = () => {

    const [name, setName] = useState('');
    const [age, setAge] = useState(0);
    const [nameNode, setNameNode] = useState();
    const count = useSelector(state => state.count)
    const persons = useSelector(state => state.persons)
    const dispatch = useDispatch();
    const addPersons = () => {
        setName(this.nameNode)
        const personObj = { id : nanoid(), name, age }
        dispatch(addPerson(personObj))


    }
    return (
        <>
            <h2>當前人有,上方和為{ count }</h2>
            <input onChange ={ c => nameNode = c.target.value } type = "test" placeholder = "輸入名字"/>
            <input onChange = { c => setAge(c.target.value) } type = "test" placeholder = "輸入年齡"/>
            <button onClick = {addPersons}>添加</button>
            <ul>
                {
                    persons.map((p)=>{
                        return <li key = {p.id}>{p.name}--{p.age}歲</li>
                    })
                }
            </ul>
        </>
    )

}

export default Person;
